const port = 5000;

const upload = multer({ dest: "uploads/" });

app.get("/", (req, res) => res.send("Hello, world"));

app.post("/uploadPost", upload.single("imgFile"), function (req, res, next) {
  console.log(req.file);
  res.redirect("http://localhost:3000/UploadPost");
});

app.use(cors());

app.listen(port, () =>
  console.log(`üî•Listening on Server: http//localhost:${port}üçì`)
);

